mapper.select:
select 

from flow_privilege privilege
where privilege.privilege_id = #{privilegeId,jdbcType=INTEGER}

mapper.update:
update flow_privilege
set privilege_name = #{privilegeName,jdbcType=VARCHAR},
  privilege_name_cn = #{privilegeNameCn,jdbcType=VARCHAR},
  privilege_value = #{privilegeValue,jdbcType=VARCHAR},
  privilege_category_id = #{privilegeCategoryId,jdbcType=INTEGER}
where privilege_id = #{privilegeId,jdbcType=INTEGER}
