mapper.select:
select 

from sec_user user
where user.id = #{id,jdbcType=INTEGER}

mapper.update:
update sec_user
set username = #{username,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  role = #{role,jdbcType=VARCHAR},
  access = #{access,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
